                setCarEnd(caarEnd);
                setTotalData(total);
            } catch (error) {
                console.log(" we cannot get the information ", error);

            }
        };

        fetchUserData();


    }, []);





    const onSubmit = () => {
        navigation.navigate('Home');
    }

    return (
        <Box backgroundColor='white' alignItems="center" mt={10} display="flex" flexDirection="column" height={'100vh'} >
            
            <Box width={"$full"} flex="1" p={4} overflow='auto'>
                {carEnd.map((car, index) => (



                    <Card key={index} size="md" variant="elevated" m="$3">
                        <Heading mb="$1" size="md">
                            {car.Name}
                        </Heading>
                        <Text size="sm">
                            {car.Price}.00 $
                        </Text>
                    </Card>

                ))}

            </Box>

            

        </Box>
    );
}


export default Bagview;
