import { Box, Divider, Button, ButtonText, View, Text, AvatarFallbackText, Avatar, Card, Heading } from '@gluestack-ui/themed';
import { useNavigation } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage'//async
import { useEffect, useState } from 'react';
import axios from 'axios';







const Bagview = () => {
    const navigation = useNavigation();
    // const token = await AsyncStorage.getItem('token');
    //console.log("token en bag", token);







    const [userData, setUserData] = useState({});
    const [FavoriteData, setFavoriteData] = useState({});
    const [productData, setProductData] = useState({});
    const [carFullData, setCarFullData] = useState({});
    const [productfinData, setProductfinData] = useState({});
    const [totalData, setTotalData] = useState(0);
    const [FavoreEnd, setFavoreEnd] = useState<any[]>([]);
    const datos: any[] = [];
    const datosfinal: any[] = [];
    let tokenString;
    let token;
    let total = 0;



    useEffect(() => {
        const fetchUserData = async () => {
            tokenString = await AsyncStorage.getItem('token');
            token = JSON.parse(tokenString);
            let idUser;
            let FavoriteUser = [];
            let FavoriteEndarr = [];



            if (token) {

                try {
                    const response = await axios.get("http://localhost/1.75/backend/public/api/Userauth", {
                        headers: {
                            'Authorization': `Bearer ${token}`
                        }
                    });
                    setUserData(response.data);
                    idUser = response.data.id;


                } catch (error) {
                    console.log(" we cannot get the information ", error);
                }
            }
            try {
                const response = await axios.get("http://localhost/1.75/backend/public/api/FavoriteIndex");

                setFavoriteData(response.data);


                for (const objeto of response.data) {

                    if (idUser == objeto.IdUserFK) {

                        FavoriteUser.push(objeto.IdFoodFK);

                    }
                }


            } catch (error) {
                console.log(" we cannot get the information ", error);
            }
            try {
                const response = await axios.get("http://localhost/1.75/backend/public/api/foodIndex");
                setProductData(response.data);


                for (const Favorite of FavoriteUser) {
                    for (const objeto of response.data) {
                        if (Favorite == objeto.id) {
                            FavoriteEndarr.push(objeto);
                        }
                    }
                }
                console.log("carr", FavoriteEndarr);
                setFavoreEnd(FavoriteEndarr);
            } catch (error) {
                console.log(" we cannot get the information ", error);

            }
        };

        fetchUserData();


    }, []);





    const onSubmit = () => {
        navigation.navigate('Home');
    }

    return (
        <Box backgroundColor='white'  mt={10} display="flex" flexDirection="column" height={'100vh'} width={"$full"} overflow='auto' >

            {FavoreEnd.map((fav, index) => (



                <Card key={index} size="md" variant="elevated" m="$3">
                    <Heading mb="$1" size="md">
                        {fav.Name}
                    </Heading>
                    <Text size="sm">
                        {fav.Price}.00 $
                    </Text>
                </Card>

            ))}



        </Box>
    );
}


export default Bagview;
