import { Ionicons } from '@expo/vector-icons';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
const Tab = createBottomTabNavigator();
import axios from 'axios';

//<--- views---> //
import FavoritesView from './FavoritesView';
import Bagview from './BagView';
import MyStack from './Components/Stack';
import SearchView from './SearchView';
import React, { useState, useEffect } from 'react';



const HomeView = () => {


    const [userData, setUserData] = useState({});
    const [carData, setCarData] = useState<any[]>([]);
    const [productData, setProductData] = useState({});
    const [carFullData, setCarFullData] = useState({});
    const [productfinData, setProductfinData] = useState({});
    const [totalData, setTotalData] = useState(0);
    const [carEnd, setCarEnd] = useState<any[]>([]);
    const datos: any[] = [];
    const datosfinal: any[] = [];
    let tokenString;
    let token;
    let total = 0;

    useEffect(() => {
        const fetchUserData = async () => {
            tokenString = await AsyncStorage.getItem('token');
            token = JSON.parse(tokenString);
            let idUser;
            let carUser = [];
            let caarEnd = [];
            let total=0;



            if (token) {

                try {
                    const response = await axios.get("http://localhost/1.75/backend/public/api/Userauth", {
                        headers: {
                            'Authorization': `Bearer ${token}`
                        }
                    });
                    setUserData(response.data);
                    idUser = response.data.id;


                } catch (error) {
                    console.log(" we cannot get the information ", error);
                }
            }
                        
        };

        fetchUserData();


    }, []);

    return (<Tab.Navigator
        screenOptions={({ route }) => ({
            tabBarIcon: ({ focused, color, size }) => {
                let iconName;
                if (route.name === 'Home') {
                    iconName = focused ? 'home' : 'home-outline';
                } else if (route.name === 'Search') {
                    iconName = focused ? 'search' : 'search-outline';
                } else if (route.name === 'Bag shop') {
                    iconName = focused ? 'bag' : 'bag-outline';
                } else if (route.name === 'Favorites') {
                    iconName = focused ? 'heart' : 'heart-outline';
                }
                return <Ionicons name={iconName} size={size} color={color} />;
            },
        })}
        tabBarOptions={{
            activeTintColor: '#FFA600',
            inactiveTintColor: 'gray',
        }}
    >
        <Tab.Screen name="Home" component={MyStack} />
        <Tab.Screen name="Search" component={SearchView} />
        <Tab.Screen name="Bag shop" component={Bagview} options={{ headerShown: false }} />
        <Tab.Screen name="Favorites" component={FavoritesView} options={{
            headerShown: true, header: () => {
                return (

                    <Avatar bgColor='$amber600' size="sm" borderRadius="$full" >
                        <AvatarFallbackText>Sandeep Srivastava</AvatarFallbackText>
                    </Avatar>

                );
            }
        }} />
    </Tab.Navigator>
    );
};
export default HomeView;
