import { Box, Text } from "@gluestack-ui/themed";
import { useEffect, useState } from "react";
import axios from "axios";
import AsyncStorage from '@react-native-async-storage/async-storage'//async




const Account = () => {
    const [userData, setUserData] = useState({});
    let tokenString;
    let token;

    useEffect(() => {
        const fetchUserData = async () => {
            tokenString = await AsyncStorage.getItem('token');
            token = JSON.parse(tokenString);
            let idUser;
            let carUser = [];
            let caarEnd = [];
            let total = 0;



            if (token) {

                try {
                    const response = await axios.get("http://localhost/1.75/backend/public/api/Userauth", {
                        headers: {
                            'Authorization': `Bearer ${token}`
                        }
                    });
                    setUserData(response.data);
                    idUser = response.data.id;


                } catch (error) {
                    console.log(" we cannot get the information ", error);
                }
            }


        };

        fetchUserData();
    }, []);

    return (
        <Box width={"$full"} height={"$full"} backgroundColor="white">
            <Box borderRadius={" 10px 10px 0 0"} height={'40%'} width={'100%'} backgroundColor='#FFA600' p={4} display='inline-block'>

                <Text position='absolute' left={"2%"} color='white' fontFamily='Arial' fontSize={"$175%"} top={"$25%"}    >{userData.Name} </Text>
                <Text position='absolute' left={"2%"} color='white' fontFamily='Arial' fontSize={"$175%"} top={"$37%"}    >{userData.FirstSurname}</Text>
                <Text position='absolute' left={"2%"} color='white' fontFamily='Arial' fontSize={"$175%"} top={"$49%"}    >{userData.SecondSurname}</Text>

            </Box>
            <Box>

                <Text position='absolute' left={"2%"} color='white' fontFamily='Arial' fontSize={"$175%"} top={"$25%"}    >{userData.PhoneNumber} </Text>
                <Text position='absolute' left={"2%"} color='white' fontFamily='Arial' fontSize={"$175%"} top={"$37%"}    >{userData.Email}</Text>
                
            </Box>

        </Box>
    );

}
export default Account;
