import { Box, Heading, ModalBody, ModalFooter, Center, Modal, ModalBackdrop, ModalContent, ModalHeader, ModalCloseButton, Icon, CloseIcon, Card, VStack, Text, Button, ButtonText, FormControl, FormControlLabel, Input, InputField, FormControlLabelText } from '@gluestack-ui/themed';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const InsertFood = () => {
    const [formData, setData] = useState({});
    const [foodData, setFoodData] = useState<any[]>([]);
    const [groupData, setGroupData] = useState<any[]>([]);

    const [showModal, setShowModal] = useState(false)
    console.log(showModal)
    const ref = React.useRef(null)


    useEffect(() => {
        const data = async () => {
            try {
                const response = await axios.get('http://localhost/1.75/backend/public/api/foodIndex');
                setFoodData(response.data);
            } catch (error) {
                console.log("error getting the groups", error);
            }
        };
        const dataGroup = async () => {
            try {
                const response = await axios.get('http://localhost/1.75/backend/public/api/FoodGroupIndex');
                setGroupData(response.data);
            } catch (error) {
                console.log("error getting the groups", error);
            }
        };
        data();
        dataGroup();
    }, []);

    const onSubmit = async () => {
        try {
            axios.post('http://localhost/1.75/backend/public/api/FoodGroupStore', formData);
            const response = await axios.get('http://localhost/1.75/backend/public/api/foodIndex');
            setFoodData(response.data);

        } catch (arror) {
            console.log("error", arror);
        }

    }
    const onDelete = async (id: string) => {
        try {
            axios.post(`http://localhost/1.75/backend/public/api/FoodGroupDestroy/${id}`);
            const response = await axios.get('http://localhost/1.75/backend/public/api/FoodGroupIndex');
            setFoodData(response.data);
            console.log("deleted");

        } catch (error) {
            console.log("not deleted");
        }
    }
    return (
        <Box height={"$full"} width={"$full"}>

            <Center h={300}>
                <Button onPress={() => setShowModal(true)} ref={ref}>
                    <ButtonText>Add</ButtonText>
                </Button>
                <Modal
                    isOpen={showModal}
                    onClose={() => {
                        setShowModal(false)
                    }}
                    finalFocusRef={ref}
                >
                    <ModalBackdrop />
                    <ModalContent>
                        <ModalHeader>
                            <Heading size="lg">Food</Heading>
                            <ModalCloseButton>
                                <Icon as={CloseIcon} />
                            </ModalCloseButton>
                        </ModalHeader>
                        <ModalBody>
                            <FormControl size={"md"} isDisabled={false} isRequired={false}>
                                <Text>Name</Text>

                                <Input>
                                    <InputField placeholder='Name' defaultValue='' type='text' onChangeText={value => setData({
                                        ...formData,
                                        Name: value
                                    })} />
                                </Input>

                            </FormControl>
                            <FormControl size={"md"} isDisabled={false} isRequired={false}>
                                <Text>Description</Text>
                                <Input>
                                    <InputField placeholder='Description' defaultValue='' type='text' onChangeText={value => setData({
                                        ...formData,
                                        Description: value
                                    })} />
                                </Input>

                            </FormControl>
                            <FormControl size={"md"} isDisabled={false} isRequired={false}>
                                <Text>Price</Text>


                                <Input>
                                    <InputField placeholder='Price' defaultValue='' type='text' onChangeText={value => setData({
                                        ...formData,
                                        Price: value
                                    })} />
                                </Input>

                            </FormControl>
                            <FormControl size={"md"} isDisabled={false} isRequired={false}>
                                <Text>Category</Text>
                                <select name="idFoodGroupFk" id="" onChange={event => setData({
                                    ...formData,
                                    idFoodGroupFk: value
                                })}>
                                    {groupData.map((group, index) => (
                                        <option key={index} value={group.Name}>{group.Name}</option>
                                    ))}

                                </select>

                            </FormControl>
                            <FormControl size={"md"} isDisabled={false} isRequired={false}>
                                <Input>
                                    <InputField type='file' onChange={event => setData({
                                        ...formData,
                                        Image: event.target.files[0]
                                    })} />
                                </Input>
                            </FormControl>

                        </ModalBody>
                        <ModalFooter>
                            <Button
                                variant="outline"
                                size="sm"
                                action="secondary"
                                mr="$3"
                                onPress={() => {
                                    setShowModal(false)
                                }}
                            >
                                <ButtonText>Cancel</ButtonText>
                            </Button>
                            <Button
                                size="sm"
                                action="positive"
                                borderWidth="$0"
                                onPress={onSubmit}
                            >
                                <ButtonText>add</ButtonText>
                            </Button>
                        </ModalFooter>
                    </ModalContent>
                </Modal>
            </Center>


            <Box width={"$full"} height={"$full"} flex="1" p={4} overflow='auto'>
                {foodData.map((food, index) => (
                    <Card key={index} size="md" variant="filled" m="$3">
                        <Box flexDirection="row" alignItems="center" justifyContent="space-between">
                            <Box>
                                <Heading mb="$1" size="md">
                                    {food.Name}
                                </Heading>

                            </Box>

                            <Button onPress={() => onDelete(food.id)} size="xs" backgroundColor='red' width={"$35px"} variant="solid" isDisabled={false} isFocusVisible={false} >
                                <ButtonText>x</ButtonText>
                            </Button>
                        </Box>
                    </Card>
                ))}
            </Box>
        </Box>

    );
}
export default InsertFood;
