import React, { useEffect, useState } from 'react';
import { getFoodItem } from '../API/Request';
import { FoodResponse, Img } from '../API/Config';
import { Box, Image, VStack , Text, Button, ButtonText} from '@gluestack-ui/themed';

const DetailScreen = ({ route }) => {
    const [userData, setUserData] = useState({});
    const [carData, setCarData] = useState<any[]>([]);
    const [productData, setProductData] = useState({});
    const [carFullData, setCarFullData] = useState({});
    const [productfinData, setProductfinData] = useState({});
    const [totalData, setTotalData] = useState(0);
    const [carEnd, setCarEnd] = useState<any[]>([]);
    const datos: any[] = [];
    const datosfinal: any[] = [];
    let tokenString;
    let token;
    let total = 0;

    const { Id } = route.params;

    const [FoodItem, SetFoodItem] = useState<FoodResponse[]>([]);

    const food = async () => {
        const data = await getFoodItem(Id);
        if (data != null) {
            SetFoodItem(data);
        }
    }

    useEffect(() => {
        food();
    }, []);

    useEffect(() => {
        if (FoodItem !== undefined) {
            console.log("que tiene FOOD Item:", FoodItem);
        }
    }, [FoodItem]);
    const addBag=()=>{

    }
    useEffect(() => {
        const fetchUserData = async () => {
            tokenString = await AsyncStorage.getItem('token');
            token = JSON.parse(tokenString);
            let idUser;
            let carUser = [];
            let caarEnd = [];
            let total = 0;



            if (token) {

                try {
                    const response = await axios.get(`${ApiUrl}Userauth`, {
                        headers: {
                            'Authorization': `Bearer ${token}`
                        }
                    });
                    setUserData(response.data);
                    idUser = response.data.id;


                } catch (error) {
                    console.log(" we cannot get the information ", error);
                }
            }
           

        };

        fetchUserData();


    }, []);

    return (
        <Box mt={"$5"} padding={"$5"} height={"$full"}>
            <Image
                size="md"
                width={"$full"}
                height={"$1/2"}
                alt="login_image"
                source={{ uri: `${Img+FoodItem.Image}` }}
                resizeMode="cover" style={{
                    alignSelf: "center"
                }} />
            <VStack>
                <Text size="lg" fontWeight="bold">{FoodItem.Name}</Text>
                <Text size="sm">{FoodItem.Description}</Text>
                <Text size="sm" fontWeight="$extrabold">Precio: {FoodItem.Price}</Text>
            </VStack>
            <Button action='primary' onPress={addBag}>
                <ButtonText>
                    Add Bag
                </ButtonText>

            </Button>
        </Box>
    );
};

export default DetailScreen;
