import { Box,Heading,Card, VStack, Text, Button, ButtonText, FormControl, FormControlLabel, Input, InputField, FormControlLabelText } from '@gluestack-ui/themed';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const InsertFood=()=>{
    const [formData, setData] = useState({});
    const [foodData, setFoodData] = useState<any[]>([]);


    useEffect(() => {
        const data = async()=>{
            try {
                const response = await axios.get('http://localhost/1.75/backend/public/api/foodIndex');
                setFoodData(response.data);               
            } catch (error) {
                console.log("error getting the groups", error);
            }
        };
        data();
    }, []);

    const onSubmit = async () => {
        try {
            axios.post('http://localhost/1.75/backend/public/api/FoodGroupStore', formData);
            const response = await axios.get('http://localhost/1.75/backend/public/api/FoodGroupIndex');
            setFoodData(response.data); 

        } catch (arror) {
            console.log("error", arror);
        }

    }
    const onDelete = async(id:string)=>{
        try {
            axios.post(`http://localhost/1.75/backend/public/api/FoodGroupDestroy/${id}`);
            const response = await axios.get('http://localhost/1.75/backend/public/api/FoodGroupIndex');
            setFoodData(response.data); 
            console.log("deleted");
            
        } catch (error) {
            console.log("not deleted");
        }
    }
    return(
        <Box height={"$full"} width={"$full"}>
            
            

            
            <Box width={"$full"} height={"$full"} flex="1" p={4} overflow='auto'>
                {foodData.map((food, index) => (
                    <Card key={index} size="md" variant="filled" m="$3">
                        <Box flexDirection="row" alignItems="center" justifyContent="space-between">
                            <Box>
                                <Heading mb="$1" size="md">
                                    {food.Name}
                                </Heading>
                                
                            </Box>

                            <Button onPress={()=>onDelete(food.id)} size="xs" backgroundColor='red' width={"$35px"} variant="solid"  isDisabled={false} isFocusVisible={false} >
                                <ButtonText>x</ButtonText>
                            </Button>
                        </Box>
                    </Card>
                ))}
            </Box>
        </Box>

    );
}
export default InsertFood;
